/**
 * Sample script to move a servo back and forth.
 */
var Board = require('../lib/firmata').Board;
var board = new Board('/dev/ttyACM0', function() {
	var degrees = 10;
	var incrementer = 10;
	board.pinMode(7,board.MODES.SERVO);
	board.servoWrite(7,0);
	setInterval(function(){
		if(degrees >= 180 || degrees === 0){
			incrementer *= -1;
		}
		degrees += incrementer;
		board.servoWrite(7,degrees);
	},500);
});
